
const validateRoute = (item) => {
	let route = item.route;
	let rejects = [];
	for (const name in item.params) {
		let param = item.params[name];
		if (param.value === undefined || String(param.value).trim() === '') {
			rejects.push(name);
		}
	}
	for (const name in item.queries) {
		let query = item.queries[name];
		let required = query.optional === false;
		if ((query.value === undefined || String(query.value).trim() === '') && required) {
			rejects.push(name);
		}
	}
	return rejects;
}
